package insightcloudsecClient

import (
	"net/http"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestWhitelabel_SetCustomImage(t *testing.T) {

	base64_encoded := "data:image/svg+xml;base64,"
	testCases := []struct {
		test_name    string
		image_url    string
		err_expected bool
	}{
		{"Valid Image URL", "", false},
		{"Valid Base64 Encoded Image", base64_encoded, false},
	}

	for _, tc := range testCases {
		t.Run(tc.test_name, func(t *testing.T) {
			setup()
			mux.HandleFunc("/v2/whitelabel/set-custom-logo", func(w http.ResponseWriter, r *http.Request) {
				assert.Equal(t, http.MethodPost, r.Method, "Expected method 'POST', got %s", r.Method)
				w.Header().Set("content-type", "application/json")
				w.WriteHeader(http.StatusOK)
			})
			if tc.err_expected {
				assert.Error(t, client.SetCustomLogo(tc.image_url))
			} else {
				assert.NoError(t, client.SetCustomLogo(tc.image_url))
			}
			teardown()
		})
	}
}
